mapper.update:
UPDATE activity_account_day
SET day_surplus = day_surplus - 1
WHERE user_id = #{userId} AND activity_id = #{activityId} AND day_key = #{dayKey}

UPDATE activity_account_day
SET day_allocate = day_allocate + #{dayAllocate},
    day_surplus = day_surplus + #{daySurplus}
WHERE user_id = #{userId} AND activity_id = #{activityId} AND day_key = #{dayKey}

mapper.select:
SELECT activity_id, user_id, day_key, day_limit, day_allocate, day_surplus
FROM activity_account_day
WHERE user_id = #{userId} AND activity_id = #{activityId} AND day_key = #{dayKey}
