mapper.select:
SELECT user_id, activity_id, total_allocate, total_surplus, day_allocate, day_surplus, month_allocate, month_surplus
FROM activity_account
WHERE user_id = #{userId} AND activity_id = #{activityId};

mapper.update:
UPDATE activity_account
SET day_surplus = #{daySurplus}
WHERE user_id = #{userId} AND activity_id = #{activityId};

UPDATE activity_account
SET month_surplus = #{monthSurplus}
WHERE user_id = #{userId} AND activity_id = #{activityId};
